import parse from '../index'
describe('Simple React Tree Parser', () => {
    it('should have name and children properties', () => {
        const result = parse(`
            <FormField>
                <PhoneNumber />
                <Input />
            </FormField>
        `);

        expect(result.name).to.be.a.string
        expect(result.children).to.be.an('array')

        expect(result).to.have.property('name', "FormField")
        expect(result.children).to.have.deep.members([
            { name: "Input" },
            { name: "PhoneNumber" }
        ]);
    })

    it('should not show duplicate', () => {
        const result = parse(`
            <FormField>
                <PhoneNumber />
                <PhoneNumber />
                <PhoneNumber />
                <PhoneNumber />
                <PhoneNumber />
                <PhoneNumber />
            </FormField>
        `);

        expect(result.children.length).to.eql(1)
    })
})